import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'; 
// This imports bootstrap css styles. You can use bootstrap or your own classes by using the className attribute in your elements.
import React, { useState, useEffect } from 'react';

const fetchWeatherData = async (lat, lon, setWeatherData, setError) => {
  try {
    const response = await fetch(`https://api.open-meteo.com/v1/forecast?latitude=${lat}&longitude=${lon}&hourly=temperature_2m,precipitation&start=now&end=now+12h`);

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();
    setWeatherData(data);
    setError(null);
  } catch (error) {
    setError('Failed to fetch weather data');
    setWeatherData(null);
  }
};

function App() {
  const cities = {
    Dallas: { lat: 32.7767, lon: -96.7970 },
    Houston: { lat: 29.7604, lon: -95.3698 },
    Austin: { lat: 30.2672, lon: -97.7431 },
  };

  const [lat, setLat] = useState('');
  const [lon, setLon] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);

  const handleLatChange = (e) => {
    setLat(e.target.value);
  };

  const handleLonChange = (e) => {
    setLon(e.target.value);
  };

  const handleSubmit = () => {
    fetchWeatherData(lat, lon, setWeatherData, setError);
  };

  return (
    <div>
      <h1>Weather App</h1>
      <label>
        Latitude:
        <input type="text" value={lat} onChange={handleLatChange} placeholder="Enter latitude" />
      </label>
      <label>
        Longitude:
        <input type="text" value={lon} onChange={handleLonChange} placeholder="Enter longitude" />
      </label>
      <button onClick={handleSubmit}>Fetch Weather</button>
      {error && <p>{error}</p>}
      {weatherData && (
        <div>
          <h2>Weather</h2>
          <p>Temperature: {weatherData.hourly.temperature_2m[0].value} Â°C</p>
          <p>Precipitation: {weatherData.hourly.precipitation[0].value} mm</p>
        </div>
      )}
    </div>
  );
}

export default App;
