import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css'; 
// This imports bootstrap css styles. You can use bootstrap or your own classes by using the className attribute in your elements.
import React, { useState, useEffect } from 'react';

const fetchWeatherData = async (lat, lon, setWeatherData, setError) => {
  try {
    const response = await fetch(`https://api.open-meteo.com/v1/forecast?latitude=${lat}&longitude=${lon}&hourly=all&start=now&end=now+1d`);
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const data = await response.json();
    setWeatherData(data);
    setError(null);
  } catch (error) {
    setError('Failed to fetch weather data');
    setWeatherData(null);
  }
};

const cities = {
  Dallas: { lat: 32.7767, lon: -96.7970 },
  Houston: { lat: 29.7604, lon: -95.3698 },
  Austin: { lat: 30.2672, lon: -97.7431 },
};

function App() {
  const [selectedCity, setSelectedCity] = useState(null);
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);
  const [cityName, setCityName] = useState('');

  const handleCitySelect = (cityName) => {
    const city = cities[cityName];
    setSelectedCity(cityName);
    fetchWeatherData(city.lat, city.lon, setWeatherData, setError);
  };

  const handleSearch = async () => {
    try {
      const response = await fetch(`https://api.open-meteo.com/v1/geocode?query=${cityName}`);
      if (!response.ok) {
        throw new Error('City not found');
      }
      const data = await response.json();
      if (data.length === 0) {
        throw new Error('City not found');
      }
      const { latitude, longitude } = data[0];
      handleCitySelect(cityName);
    } catch (error) {
      setError(`Could not find weather for ${cityName}`);
      setWeatherData(null);
    }
  };

  return (
    <div>
      <h1>Weather App</h1>
      <div>
        {Object.keys(cities).map((cityName) => (
          <button key={cityName} onClick={() => handleCitySelect(cityName)}>
            {cityName}
          </button>
        ))}
      </div>
      <div>
        <input type="text" placeholder="Enter city name" value={cityName} onChange={(e) => setCityName(e.target.value)} />
        <button onClick={handleSearch}>+</button>
      </div>
      {error && <p>{error}</p>}
      {weatherData && (
        <div>
          <h2>Weather for {selectedCity}</h2>
          <ul>
            {Object.keys(weatherData.hourly).map((hour) => (
              <li key={hour}>Hour {hour}: {weatherData.hourly[hour].temperature_2m.value} Â°C</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
